import React from 'react';
import { useGameState } from './GameState';
import { Log } from './Log';
import { Board } from './Board';
import { Row, Column } from './Layout';

function Game() {

    const onChangePlayerX = (e: React.FormEvent<HTMLInputElement>) => {
        setPlayers(e.currentTarget.value, '');
    }

    const onChangePlayerO = (e: React.FormEvent<HTMLInputElement>) => {
        setPlayers('', e.currentTarget.value);
    }

    const {
        gameState,
        current,
        xIsNext,
        winner,
        handleClick,
        jumpTo,
        setPlayers,
        setPlayerX,
        setPlayerO,
    } = useGameState();

    return (
        <div style={{
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            padding: 100,
        }}>
            <Row gap={20}>
                <Column gap={20}>
                    <h1>Tic Tac Toe</h1>
                    <div>
                        {
                            winner
                                ? `Winner ${winner}`
                                : `Next Player ${xIsNext ? 'X' : 'O'}`
                        }
                    </div>
                    <Board board={current} onClick={handleClick} />
                    <form onSubmit={() => setPlayers}>
                        <input type="text" placeholder='enter player X' name='playerX' onChange={onChangePlayerX}/>
                        <input type="text" placeholder='enter player O' name='playerO' onChange={onChangePlayerO}/>
                        <button type="submit" onClick={()=> setPlayers} >Edit Player</button>
                    </form>
                </Column>
                <Log history={gameState.history} jumpTo={jumpTo} />
            </Row>
        </div>
    );
}

export default Game;
