import React from 'react';
import styled from 'styled-components';
import { useGameState, BoardState, Value } from './GameState';

type LayoutProps = {
    gap: number
}

const Row = styled.div<LayoutProps>`
    display: flex;
    flex-direction: row;
    gap: ${(props) => props.gap}px;
`;

const Column = styled.div<LayoutProps>`
    display: flex;
    flex-direction: column;
    gap: ${(props) => props.gap}px;
`;


function Game() {

    const {
        gameState,
        current,
        xIsNext,
        winner,
        handleClick,
        jumpTo,
    } = useGameState();

    return (
        <Row gap={20}>
            <Column gap={20}>
                <div>
                    {
                        winner
                            ? `Winner ${winner}`
                            : `Next Player ${xIsNext ? 'X' : 'O'}`
                    }
                </div>
                <Board board={current} onClick={handleClick}/>
            </Column>
            <Log />
        </Row>
    );
}

type BoardProps = {
    board: BoardState,
    onClick: (square: number ) => void,
}

function Board({board, onClick}: BoardProps) {
    return (
        <Column gap={0}>
            <Row gap={0}>
                <Square value={board[0]} onClick={() => onClick(0)}/>
                <Square value={board[1]} onClick={() => onClick(1)}/>
                <Square value={board[2]} onClick={() => onClick(2)}/>
            </Row>
            <Row gap={0}>
                <Square value={board[3]} onClick={() => onClick(3)}/>
                <Square value={board[4]} onClick={() => onClick(4)}/>
                <Square value={board[5]} onClick={() => onClick(5)}/>
            </Row>
            <Row gap={0}>
                <Square value={board[6]} onClick={() => onClick(6)}/>
                <Square value={board[7]} onClick={() => onClick(7)}/>
                <Square value={board[8]} onClick={() => onClick(8)}/>
            </Row>
        </Column>
    );
}

const StyledSquare = styled.button`
  width: 34px;
  height: 34px;
  background: #fff;
  border: 1px solid #999;
  padding: 0;
  font-size: 24px;
  font-weight: bold;
`;

type SqaureProps = {
    value: Value,
    onClick: ()=> void,
}

function Square(props: SqaureProps) {
    return (
        <StyledSquare onClick={props.onClick}>
            {props.value}
        </StyledSquare>
    );
}

function Log() {
    return (
        <ol>
            <li><button>Go to move</button></li>
        </ol>
    );
}

export default Game;
