import React from 'react';
import { useGameState } from './GameState';
import { Log } from './Log';
import { Board } from './Board';
import { Row, Column } from './Layout';

function Game() {

    const {
        gameState,
        current,
        xIsNext,
        winner,
        handleClick,
        jumpTo,
        setPlayers,
    } = useGameState();

    return (
        <div style={{
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            padding: 200,
        }}>
            <Row gap={30}>
                <Column gap={30}>
                    <div>
                        {
                            winner
                                ? `Winner ${winner}`
                                : `Next Player ${xIsNext ? 'X' : 'O'}`
                        }
                    </div>
                    <Board board={current} onClick={handleClick} />
                    <button>edit</button>
                </Column>
                <Log history={gameState.history} jumpTo={jumpTo} />
            </Row>
        </div>
    );
}

export default Game;
