import { useState } from "react";

export type Value = 'X' | 'O' | null;

export type BoardState = Value[];
const createBoardState = () => Array<Value>(9).fill(null);

function calculateWinner(boardState: BoardState) {
    const winningCombinations = [
      [0, 1, 2],
      [3, 4, 5],
      [6, 7, 8],
      [0, 3, 6],
      [1, 4, 7],
      [2, 5, 8],
      [0, 4, 8],
      [2, 4, 6]
    ];
    for (let i = 0; i < winningCombinations.length; i++) {
      const [a, b, c] = winningCombinations[i];
      if (boardState[a] && boardState[a] === boardState[b] && boardState[a] === boardState[c]) {
        boardState[a] == 'X' ? setWinner(X) : setWinner(O);
        return boardState[a];
      }
    }
    return null;
  }

  export type GameState = {
      history: BoardState[],
      step: number,
      playerX: string,
      playerO: string,
      winner: string,
  }

  export function useGameState() {
      const [gameState, setGameState] = useState<GameState>({
        history: [createBoardState()],
        step: 0,
        playerX: 'X',
        playerO: 'O',
        winner: '',
      });

      const current = gameState.history[gameState.step];
      const xIsNext = (gameState.step % 2) === 0;
      const winner = calculateWinner(current);
      var X = gameState.playerX;
      var O = gameState.playerO;
      var winningPlayer  = gameState.winner;

      //winner == 'X' ? setWinner(X) : setWinner(O)


      function setPlayerX(playerX: string){
        setGameState({
          history: gameState.history,
          step: gameState.step,
          playerX,
          playerO: gameState.playerO,
          winner: gameState.winner,
        });
        X = playerX;
      }

      function setWinner(winner: string){
        setGameState({
          history: gameState.history,
          step: gameState.step,
          playerX: gameState.playerX,
          playerO: gameState.playerO,
          winner,
        });
        console.log(winner);
      }

      function setPlayerO(playerO: string){
        setGameState({
          history: gameState.history,
          step: gameState.step,
          playerX: gameState.playerX,
          playerO,
          winner: gameState.winner,
        });
        O = playerO;
      }

      function handleClick(square: number) {
        const history = gameState.history.slice(0, gameState.step + 1);
        const boardState = history[history.length - 1];
        if (calculateWinner(boardState) || boardState[square]) {
          //record winning board in database 
          //with the name of the winner 
          return;
        }
        const newBoardState = boardState.slice();
        newBoardState[square] = (gameState.step % 2) === 0 ? 'X' : 'O';
        history.push(newBoardState);
        setGameState({
          history: history,
          step: history.length - 1,
          playerX: gameState.playerX,
          playerO: gameState.playerO,
          winner: gameState.winner,
        });
      }

      function jumpTo(step: number) {
        setGameState({
          history: gameState.history,
          step,
          playerX: gameState.playerX,
          playerO: gameState.playerO,
          winner: gameState.winner,
        });
      }

      return {
          gameState, 
          current,
          xIsNext,
          winner,
          X,
          O,
          winningPlayer,
          handleClick,
          jumpTo,
          setPlayerX,
          setPlayerO,
      };
  }
